INSERT INTO categories (name) VALUES 
('Свежие грибы'),
('Сушеные грибы'),
('Маринованные грибы'),
('Лекарственные грибы'),
('Грибница (Мицелий)'),
('Инвентарь')
INSERT INTO mushroom_reference (name, display_name, is_edible, description, cooking_tips) VALUES 
('bay_bolete', 'Польский гриб', true, 'Отличный съедобный гриб. Похож на белый, но при надавливании на трубчатый слой синеет.', 'Подходит для сушки, маринования, жарки и супов.'),
('brown_birch_bolete', 'Подберезовик', true, 'Растет в лиственных лесах. Шляпка от светло-серой до темно-коричневой. Мякоть на срезе слегка темнеет.', 'Отличен в жареном виде и в супах. При сушке чернеет.'),
('chanterelle', 'Лисичка', true, 'Ярко-желтый гриб с волнистой шляпкой. Никогда не бывает червивым. Растет группами.', 'Идеальны для жарки с картошкой. Не требуют предварительного отваривания.'),
('charcoal_burner', 'Сыроежка сине-желтая', true, 'Шляпка разноцветная (зеленая, фиолетовая). Пластинки гибкие, не ломаются при прикосновении.', 'Универсальный гриб: жарка, тушение, засолка.'),
('common_morel', 'Сморчок настоящий', true, 'Весенний гриб со сморщенной ячеистой шляпкой, похожей на соты.', 'ОБЯЗАТЕЛЬНО отваривать 15-20 минут, отвар слить! Затем можно жарить.'),
('deathcap', 'Бледная поганка', false, 'СМЕРТЕЛЬНО ЯДОВИТЫЙ. Имеет юбочку на ножке и мешочек у основания. Пластинки всегда белые.', 'КАТЕГОРИЧЕСКИ НЕЛЬЗЯ ЕСТЬ! Смертельно опасен.'),
('false_chanterelle', 'Ложная лисичка', false, 'Похожа на лисичку, но цвет более оранжевый, ножка тоньше. Растет на гнилой древесине.', 'Слабоядовитый гриб низкого качества. Лучше не собирать.'),
('field_mushroom', 'Шампиньон полевой', true, 'Белая шляпка, пластинки розовые (у старых коричневые). На ножке кольцо.', 'Можно есть сырым, жарить, варить. Не путать с бледной поганкой!'),
('fly_agaric', 'Мухомор красный', false, 'Красная шляпка с белыми точками. Ядовит.', 'Несъедобен. В пищу употреблять опасно.'),
('giant_puffball', 'Головач гигантский', true, 'Огромный белый шар. Съедобен, пока мякоть внутри идеально белая.', 'Жарить ломтиками в панировке. Если пожелтел внутри — не есть.'),
('honey_fungus', 'Опенок осенний', true, 'Растет семьями на пнях. На ножке есть кольцо. Шляпка с чешуйками.', 'Требует отваривания перед жаркой (20 мин). Хорош для маринования.'),
('orange_birch_bolete', 'Подосиновик', true, 'Ярко-оранжевая шляпка. На срезе ножка синеет, затем чернеет.', 'Очень вкусный гриб. Для супов, жарки, маринования.'),
('oyster_mushroom', 'Вешенка', true, 'Растет на деревьях. Цвет серый. Вкус мягкий.', 'Ножки жесткие (обрезать). Шляпки жарить или тушить.'),
('parasol', 'Гриб-зонтик пестрый', true, 'Крупный гриб на тонкой ножке. Шляпка в чешуйках. Кольцо двигается.', 'Шляпку жарить в кляре целиком. Ножку не едят.'),
('penny_bun', 'Белый гриб', true, 'Царь грибов. Толстая ножка, коричневая шляпка. Мякоть белая.', 'Универсален. Суп, жарка, маринад.'),
('saffron_milkcap', 'Рыжик', true, 'Оранжевый гриб. Выделяет оранжевый сок, который зеленеет.', 'Король засолки. Также можно жарить.'),
('slippery_jack', 'Масленок', true, 'Скользкая маслянистая шляпка. Растет в хвойных лесах.', 'Снимать кожицу со шляпки перед готовкой. Мариновать или варить суп.'),
('the_sickener', 'Сыроежка жгуче-едкая', false, 'Ярко-красная шляпка. Жгучий перечный вкус.', 'Несъедобна. Вызывает тошноту.'),
('woolly_milkcap', 'Волнушка розовая', true, 'Розовая шляпка с мохнатыми краями. Выделяет белый горький сок.', 'Только в засолку после вымачивания (3 суток).'),
('yellow_stainer', 'Шампиньон рыжеющий', false, 'Двойник шампиньона. При надавливании БЫСТРО желтеет. Пахнет аптекой/фенолом.', 'Ядовит! Вызывает расстройство желудка.');
